import React, { Component } from 'react';
import { get } from 'axios';
import $ from 'jquery';

window.jQuery = $;
window.$ = $;
global.jQuery = $;

export class Home extends Component {
  static displayName = Home.name;

  constructor() {
      super();
      this.state = {
          firstNum : 0,
          secondNum : 0,
          action : "+",
          result : 0,
          history: []
      };
      this.calc = this.calc.bind(this);
  }
  
  componentWillMount() {

      get("/api/getHistory").then((result) => {
          this.setState({history: result})
      });
      
  }

    calc(){
      alert(1)
      const firstNum = this.state.firstNum;
      const secondNum = this.state.secondNum;
      const action = this.state.action;
      
      get("/api/calc?firstNum=" + firstNum + "&secondNum=" + secondNum + "&action=" + action).then((result) => {
          
          this.setState({result: result.Result});
          this.setState({history: result.History})
      });
  }
  
  
  
  render () {
      
    return (
      <div>
          <br/>
          <br/>
        <div>
          <input type="number" id="FirstNum" value={this.state.firstNum} onChange={() => this.setState({firstNum: $('#FirstNum').val()})} />
          &nbsp;&nbsp;
          <select id="Action" onChange={() => this.setState({action: $('#Action').val()})}>
              <option value="+">+</option>
              <option value="-">-</option>
              <option value="X">X</option>
              <option value=":">:</option>
          </select>
          &nbsp;&nbsp;
          <input type="number" id="SecondNum" value={this.state.secondNum} onfocusout={() => this.calc()} />
          &nbsp;&nbsp;
          =
          &nbsp;&nbsp;
          <input type="number" readOnly={true} id="Result" value={this.state.result} />
      </div>
        <div>
            <b>Calculation History:</b>
            <br/>
            <ul>
            {
                
                this.state.history === "" ? JSON.parse(this.state.history).map((element,index) => {
                    return <li>{element.firstNum} {element.action} {element.secondNum} = {element.result}</li>   
            }) : ""
            }
            </ul>
        </div>
      </div>
    );
  }
}
